using UnityEngine;using CoinFlipGames.FSM;using System.Collections;public class BlobState : MonoBehaviour {    public StateMachine behaviors;    private BlobBehavior behaviorScript;    public enum states    {        Attacking,        Fleeing,        Dead    }	// Use this for initialization	void Start () {        behaviorScript = GetComponent<BlobBehavior>();        behaviors.AddState(            states.Attacking,            (previous) => {            },            behaviorScript.Attack,            (next) => {            }        );        behaviors.AddState(            states.Fleeing,            (previous) => {            },            behaviorScript.Flee,            (next) => {            }        );    }    // Update is called once per frame    void Update () {		}}