using UnityEngine;using CoinFlipGames.FSM;using System.Collections;public class BlobState : MonoBehaviour {    public StateMachine behaviors;    private BlobBehavior behaviorScript;    public enum states    {        Attacking,        Fleeing,        Dead    }	// Use this for initialization	void Start () {        behaviorScript = GetComponent<BlobBehavior>();        behaviors.AddState(            states.Attacking,            (previous) => {                Debug.Log("attacking bro");            },            behaviorScript.Attack,            (next) => {                Debug.Log("I am not longer attacking! Bro!");            }        );        behaviors.AddState(            states.Fleeing,            (previous) => {                Debug.Log("fleeing");            },            behaviorScript.Flee,            (next) => {                Debug.Log("no longer fleeing");            }        );    }    // Update is called once per frame    void Update () {		}}