using UnityEngine;using System.Collections;public class GameManager : MonoBehaviour {    public float spawnTimeout = 10f;    public GameObject blobPrefab;    public GameObject environment;    private GameObject[] spawns;    private float spawnTimer;    // Use this for initialization    void Start () {        spawns = GameObject.FindGameObjectsWithTag("BlobSpawn");        spawnTimer = spawnTimeout;	}		// Update is called once per frame	void Update () {        spawnTimer += Time.deltaTime;        if (spawnTimer >= spawnTimeout) {            GameObject spawn = spawns[Random.Range(0, spawns.Length)];            GameObject enemy = (GameObject)Instantiate(blobPrefab, new Vector3(spawn.transform.position.x, spawn.transform.position.y, -5), Quaternion.identity);            enemy.tag = "Enemy";            spawnTimer = 0f;        }    }}